     <div class="value-proposition">

         <h2 class="header-title">Working Together to Keep Us Apart</h2>
         <p class="intro">Use our branch booking tool to reserve your spot in 
             line for quick visits with one of our tellers.
            Show up at your booked time to avoid line-ups and help us limit 
            our foot traffic at our branches.
         </p>
         <button type="button" id="view-availability" name="availability">View Availability</button>
     </div>

     <div id="booking-tool">
        <h2 class="booking-title">Book your time with a teller.</h2>
        <form method="POST" action="confirmation.php">
            <table class="booking-table">
                <tr>
                    <!-- first name -->
                    <td>
                        <input type="text" id="first-name" placeholder="First Name" required>
                    </td>
                    <!-- branch -->
                    <td>
                        <select selected id="branch-selected" name="branch-selected">
                            <option <?php if(isset($_SESSION['branch-prepop']) && $_SESSION['branch-prepop'] == "Branch 1") echo "selected=\"selected\"";?>>Branch 1</option>
                            <option <?php if(isset($_SESSION['branch-prepop']) && $_SESSION['branch-prepop'] == "Branch 2") echo "selected=\"selected\"";?>>Branch 2</option>
                            <option <?php if(isset($_SESSION['branch-prepop']) && $_SESSION['branch-prepop'] == "Branch 3") echo "selected=\"selected\"";?>>Branch 3</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <!-- last name -->
                    <td>
                        <input type="text" id="last-name" placeholder="Last Name" required>
                    </td>
                    <!-- date -->
                    <td>
                        <select selected id="date-selected" name="date-selected">
                            <option>May 1, 2020</option>
                            <option>May 2, 2020</option>
                            <option>May 3, 2020</option>
                        </select>
                    </td>
                </tr>
                <tr>
                    <!-- client card number -->
                    <td>
                        <input type="text" id="client-card-number" placeholder="Client Card Number (Optional)">
                    </td>
                    <!-- time -->
                    <td>
                        <select selected id="time-selected" name="time-selected">
                            <option>9:00 AM</option>
                            <option>9:10 AM</option>
                            <option>9:20 AM</option>
                            <option>9:30 AM</option>
                            <option>9:40 AM</option>
                            <option>9:50 AM</option>
                            <option>10:00 AM</option>
                            <option>10:10 AM</option>
                            <option>10:20 AM</option>
                            <option>10:30 AM</option>
                            <option>10:40 AM</option>
                            <option>10:50 AM</option>
                            <option>11:00 AM</option>
                        </select>
                    </td>
                </tr>
            </table>
            <input type='submit' id='book-now' value='Book Now'>
    </form>
     </div>

